'use strict';
module.exports = function(sequelize, DataTypes) {
  var Assessment = sequelize.define('Assessment', {
    assessment_task: DataTypes.STRING,
    assessment_status: DataTypes.STRING,
    CandidateId: DataTypes.INTEGER
  }, {
    classMethods: {
      associate: function(models) {
        Assessment.belongsTo(models.Candidate);
      }
    }
  });
  return Assessment;
};