'use strict';
module.exports = function(sequelize, DataTypes) {
  var Candidate = sequelize.define('Candidate', {
    candidate_name: DataTypes.STRING,
    candidate_created: DataTypes.DATE,  
    RecruiterId: DataTypes.INTEGER,
    RecgroupId: DataTypes.INTEGER,
    task_start: DataTypes.DATE,
    task_end: DataTypes.DATE,
    task_owner: DataTypes.STRING,
    task_status: DataTypes.STRING,
    task_comment: DataTypes.STRING,
    assessment_task: DataTypes.STRING,
    assessment_status: DataTypes.STRING,
    career_level: DataTypes.STRING  
  }, {
    classMethods: {
      associate: function(models) {
        // associations can be defined here
          Candidate.belongsTo(models.Recruiter);
          // one to one?
      }
    }
  });
  return Candidate;
};