'use strict';
module.exports = function(sequelize, DataTypes) {
  var Counselor = sequelize.define('Counselor', {
    counselor_name: DataTypes.STRING,
    counselor_firstname: DataTypes.STRING,
    counselor_active: DataTypes.BOOLEAN
  }, {
    classMethods: {
      associate: function(models) {
        // associations can be defined here
          Counselor.hasMany(models.Employee);
      }
    }
  });
  return Counselor;
};