'use strict';
module.exports = function(sequelize, DataTypes) {
  var Employee = sequelize.define('Employee', {
    emp_name: DataTypes.STRING,
    emp_firstname: DataTypes.STRING,
    emp_role: DataTypes.STRING,
    emp_birthday: DataTypes.DATEONLY,
    emp_gender: DataTypes.CHAR(1),
    emp_start_date: DataTypes.DATEONLY,
    emp_end_date: DataTypes.DATEONLY,
    emp_status: DataTypes.STRING,
    emp_address: DataTypes.STRING,
    emp_mobile: DataTypes.STRING,
    emp_phone: DataTypes.STRING,
    emp_email: DataTypes.STRING,
    emp_emergency: DataTypes.STRING,
    emp_contract: DataTypes.STRING,
    emp_contract_no: DataTypes.STRING 
      
  }, {
    classMethods: {
      associate: function(models) {
        //verbinde logisch mit vellow und counselor
          Employee.belongsTo(models.Vellow);
          Employee.belongsTo(models.Counselor);
      }
    }
  });
  return Employee;
};