'use strict';
module.exports = function(sequelize, DataTypes) {
  var Recruiter = sequelize.define('Recruiter', {
    recruiter_name: DataTypes.STRING,
    RecruitergroupId: DataTypes.INTEGER
  }, {
    classMethods: {
      associate: function(models) {
        Recruiter.belongsTo(models.Recruitergroup);
        Recruiter.hasMany(models.Candidate);  
      }
    }
  });
  return Recruiter;
};