'use strict';
module.exports = function(sequelize, DataTypes) {
  var Vellow = sequelize.define('Vellow', {
    vellow_firstname: DataTypes.STRING,
    vellow_name: DataTypes.STRING,
    vellow_status: DataTypes.BOOLEAN
  }, {
    classMethods: {
      associate: function(models) {
        // logische verkn√ºpfung mit Employee
          Vellow.hasMany(models.Employee);
      }
    }
  });
  return Vellow;
};